-- ============================================
-- PRODUKTIONEN
-- ============================================

CREATE TABLE IF NOT EXISTS productions (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(100) NOT NULL UNIQUE,
    name_de VARCHAR(100) NOT NULL,
    category VARCHAR(50) NOT NULL,
    building_cost DECIMAL(12,2) NOT NULL,
    maintenance_cost DECIMAL(10,2) DEFAULT 0,
    production_time INT DEFAULT 3600 COMMENT 'Sekunden pro Zyklus',
    required_research_id INT DEFAULT NULL,
    required_level INT DEFAULT 1,
    icon VARCHAR(100) DEFAULT NULL,
    description TEXT,
    is_active BOOLEAN DEFAULT TRUE,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- ============================================
-- PRODUKTIONS-INPUTS
-- ============================================

CREATE TABLE IF NOT EXISTS production_inputs (
    id INT AUTO_INCREMENT PRIMARY KEY,
    production_id INT NOT NULL,
    product_id INT NOT NULL,
    quantity INT DEFAULT 1,
    is_optional BOOLEAN DEFAULT FALSE,
    FOREIGN KEY (production_id) REFERENCES productions(id) ON DELETE CASCADE,
    FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE CASCADE,
    UNIQUE KEY unique_input (production_id, product_id)
);


-- ============================================
-- PRODUKTIONS-OUTPUTS
-- ============================================

CREATE TABLE IF NOT EXISTS production_outputs (
    id INT AUTO_INCREMENT PRIMARY KEY,
    production_id INT NOT NULL,
    product_id INT NOT NULL,
    quantity INT DEFAULT 1,
    probability DECIMAL(3,2) DEFAULT 1.00 COMMENT '0.00-1.00 Wahrscheinlichkeit',
    FOREIGN KEY (production_id) REFERENCES productions(id) ON DELETE CASCADE,
    FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE CASCADE,
    UNIQUE KEY unique_output (production_id, product_id)
);


